import React, { Component } from "react";
import logo from "../images/logo.png";
import user from "../images/user.png";
import Sidebar from "./Sidebar";
import logout from "../images/logout.png";
import { NavLink } from "react-router-dom";
import axios from "axios";

const Employee = props => (
	<tr>
		<td>{props.employee.id}</td>
		<td>{props.employee.fname}</td>
		<td>{props.employee.email}</td>
		<td>{props.employee.role}</td>
	</tr>
);

class employee extends Component {
	constructor(props) {
		super(props);
		this.state = { employees: [] };
	}

	componentDidMount() {
		axios
			.get("http://localhost:3004/employee/")
			.then(response => {
				this.setState({ employees: response.data });
			})
			.catch(function (error) {
				console.log(error);
			});
	}

	employeeList() {
		return this.state.employees.map(function (currentEmployee, i) {
			return <Employee employee={currentEmployee} key={i} />;
		});
	}

	render() {
		return (
			<div>
				<div className='MainContainer'>
					<div className='containermini'>
						<img src={logo} className='logo' />

						<img src={user} className='user' />

						<Sidebar />

						<img src={logout} className='logout' />
					</div>
					<div className='emp-list-body'>
						<div className='emp-list-body2'>
							<div className='emp-list-title'>
								<h2 className='emp-list-heading'>Employees</h2>
							</div>
							<div className='emp-list-search-btn'>
								<input
									type='search'
									className='emp-list-search'
									placeholder='search'
								/>
								<NavLink
									to='/salary'
									activeClassName='active-nav'
									className='row'>
									<button className='emp-list-btn'>add new</button>
								</NavLink>
							</div>
							<br />
							<table className='emp-list-table'>
								<thead>
									<tr>
										&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;
										<th className='emp-list-table-head'>ID</th>
										&nbsp; &nbsp;&nbsp;
										<th className='emp-list-table-head'>first name</th>
										&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;
										&nbsp;
										<th className='emp-list-table-head'>email</th>
										&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;
										&nbsp;
										<th className='emp-list-table-head'>role</th>
										&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;
									</tr>
								</thead>
								<tbody>{this.employeeList()}</tbody>
							</table>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default employee;
