import React, { useEffect, useState } from "react";
import Sidebar from "./Sidebar";
import logo from "../images/logo.png";
import user from "../images/user.png";
import logout from "../images/logout.png";
import "./Employee2.css";
import axios from "axios";
import { NavLink } from "react-router-dom";

function EmployeeList(props) {
	const [id, setId] = useState("");
	const [fname, setFname] = useState("");
	const [email, setEmail] = useState("");
	const [role, setRole] = useState("");

	return (
		<div>
			<div>{id}</div>
			<div>{fname}</div>
			<div>{email}</div>
			<div>{role}</div>
		</div>
	);
}

function Employee2() {
	const [employees, setEmployees] = useState([]);

	useEffect(() => {
		axios
			.get("http://localhost:3004/employee/")
			.then(response => {
				setEmployees(response.data);
			})
			.catch(function (error) {
				console.log(error);
			});
	}, []);

	const openPopup = e => {
		console.log(e.target);
	};

	return (
		<div>
			<div className='MainContainer'>
				<div className='containermini'>
					<img src={logo} className='logo' />

					<img src={user} className='user' />

					<Sidebar />

					<img src={logout} className='logout' />
				</div>
				<div className='emp-list-body'>
					<div className='emp-list-body2'>
						<div className='emp-list-title'>
							<h2 className='emp-list-heading'>Employees</h2>
						</div>
						<div className='emp-list-search-btn'>
							<input
								type='search'
								className='emp-list-search'
								placeholder='search'
							/>
							<NavLink
								to='/new-employee'
								activeClassName='active-nav'
								className='row'>
								<button className='emp-list-btn'>add new</button>
							</NavLink>
						</div>
						<br />
						<div className='emp-list-table2'>
							<div>
								<h5>Id</h5>
								<h5>First Name</h5>
								<h5>Email</h5>
								<h5>Role</h5>
							</div>
							{employees.map((employee, index) => {
								return (
									<div key={index} data-id={employee.id} onClick={openPopup}>
										<div clickable={false}>{employee.id}</div>
										<div>{employee.fname}</div>
										<div>{employee.email}</div>
										<div>{employee.role}</div>
									</div>
								);
							})}
						</div>
					</div>
				</div>
			</div>
		</div>
	);
}

export default Employee2;
